const SynonymsButton = (props) => {
    const {datamuseRequest,input, generate, setOutputWord} = props;

    function getUrl(ml) {
        return `https://api.datamuse.com/words?ml=`+input;
    };

    function showSynonyms(output) {

        const ShowList = [];

        if (output.length) {
            ShowList.push(<h2 key="titleSyn">Words with a similar meaning to {input}: </h2>)
            ShowList.push(<ul key='ul'>{generate(output)}</ul>);
        } else {
            ShowList.push(<p>no result</p>);
        }
        setOutputWord(ShowList);
    };

    const synonyms = () => {
        datamuseRequest(getUrl(input),showSynonyms)
    };

    return (
        <button onClick={synonyms} type="button" className="btn btn-secondary">Show synonyms</button>
    )
};

export default SynonymsButton;
