const SynonymsButton = (props) => {
    const {datamuseRequest,input, generateWords, OutputWord} = props;

    function getUrl(ml) {
        return 'https://api.datamuse.com/words?ml='+input;
        // return `https://api.datamuse.com/words?${(new URLSearchParams({'ml': inputWords})).toString()}`;
    };

    function showSynonyms(output) {

        const wordToShow = [];

        if (output.length !== 0 ) {

            wordToShow.push(<h2 key="titleSyn">Words with a similar meaning to {inputWords}: </h2>)
            wordToShow.push(<ul key='ul'>{generateWords(output)}</ul>);

        } else {
            wordToShow.push(<p>no result</p>);
        }

        OutputWord(wordToShow);

    };

    const synonymsBtt = () => {
        datamuseRequest(getUrl(input),showSynonyms)
    };

    return (
        <button onClick={synonymsBtt} type="button" className="btn btn-secondary" id="show_synonyms">Show synonyms</button>
    )
};

export default SynonymsButton;
